import "./style.css";
import React, { useEffect, useState } from "react";
import axios from "axios"; // Axios 추가
import { URL, serverUrl } from "./constants/constants";
import { Client, Frame } from "@stomp/stompjs";
import SockJS from "sockjs-client";

enum SocketType {
  GAME_INFO = "GAME_INFO",
  GAME_END = "GAME_END",
  CHAT = "CHAT",
  QUIZ = "QUIZ",
  QUIZ_TIME_OVER = "QUIZ_TIME_OVER",
  PLAYER_OUT = "PLAYER_OUT",
}

interface receiveMessage {
  socketType: SocketType;
  gameStatus: GameStatus;
  data: any;
  message: string;
}

interface sendMessage {
  socketType: SocketType;
  message: string;
}

interface GameStatus {
  gameId: bigint;
  curRound: number;
  playerA: Player;
  playerB: Player;
  question: string;
  answer: string;
  quizEndTime?: Date;
}

interface Player {
  userId: bigint;
  ranking: bigint;
  point: bigint;
  winCnt: number;
  profile: string;
}

const socket = new SockJS(URL);
const stompClient = new Client({ webSocketFactory: () => socket });

stompClient.activate();

function MatchingGamePage() {
  const [gameId, setGameId] = useState<string | null>(null);

  const messageToSend: sendMessage = {
    socketType: SocketType.GAME_INFO,
    message: "hihi",
  };

  // const startGame = async () => {
  //   try {
  //     const response = await axios.post("/api/start-game"); // Axios를 사용한 요청
  //     setGameId(response.data.gameId); // 게임 아이디 설정
  //     subscribeToChatRoom(response.data.gameId); // 웹 소켓 구독 시작
  //   } catch (error) {
  //     console.error("Error starting the game:", error);
  //   }
  // };

  useEffect(() => {
    // 컴포넌트가 마운트될 때, 웹 소켓을 사용할 준비
    stompClient.activate();
    axios
      .get(serverUrl + "/api/game/wait")
      .then((response) => {
        setGameId(response.data.gameId);

        // 게임 아이디를 받아온 후, 웹 소켓을 초기화하고 구독합니다.
        const subscription = subscribeToChatRoom(response.data.gameId);

        return () => {
          // 컴포넌트가 언마운트될 때 구독을 해제
          subscription.unsubscribe();
        };
      })
      .catch((error) => {
        // 오류 처리
      });

    return () => {
      // 컴포넌트가 언마운트될 때, 웹 소켓 연결 해제
      stompClient.deactivate();
    };
  }, []);

  return (
    <div className="matching-game-container">
      <button onClick={startGame}>Start Game</button>
    </div>
  );
}

function subscribeToChatRoom(gameId: string) {
  return stompClient.subscribe(`/topic/game.${gameId}`, (message: Frame) => {
    const receivedMessage: receiveMessage = JSON.parse(message.body);
    console.log("Received message:", receivedMessage);
  });
}

function publishMessageToChatRoom(gameId: string, message: sendMessage) {
  stompClient.publish({
    destination: `/pub/game.${gameId}`,
    body: JSON.stringify(message),
  });
}

export default MatchingGamePage;
